// TrainerPage.js
import React, { useEffect, useState } from 'react';
import { auth, db } from '../firebase';
import { onAuthStateChanged } from 'firebase/auth';
import { doc, getDoc, setDoc, updateDoc } from 'firebase/firestore';
import './TrainerPage.css';

const TrainerPage = () => {
  const [user, setUser] = useState(null);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [availableSlots, setAvailableSlots] = useState([]);
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [message, setMessage] = useState('');

  // Listen for auth state
  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      if (currentUser) {
        setUser(currentUser);
        setEmail(currentUser.email);
        // Fetch trainer details
        fetchTrainerDetails(currentUser.uid);
      }
    });
    return () => unsubscribe();
  }, []);

  const fetchTrainerDetails = async (uid) => {
    try {
      const docRef = doc(db, 'trainers', uid);
      const docSnap = await getDoc(docRef);
      if (docSnap.exists()) {
        const data = docSnap.data();
        setName(data.name || '');
        setAvailableSlots(data.availableSlots || []);
      }
    } catch (err) {
      console.error('Error fetching trainer details:', err);
    }
  };

  const handleAddSlot = async () => {
    if (!date || !time) return;
    const newSlots = [...availableSlots, { date, time }];
    try {
      const docRef = doc(db, 'trainers', user.uid);
      await setDoc(docRef, { name, email, availableSlots: newSlots }, { merge: true });
      setAvailableSlots(newSlots);
      setDate('');
      setTime('');
      setMessage('Slot added successfully');
    } catch (err) {
      console.error('Error adding slot:', err);
      setMessage('Failed to add slot');
    }
  };

  const handleNameChange = async () => {
    if (!name) return;
    try {
      const docRef = doc(db, 'trainers', user.uid);
      await setDoc(docRef, { name, email, availableSlots }, { merge: true });
      setMessage('Name updated successfully');
    } catch (err) {
      console.error('Error updating name:', err);
      setMessage('Failed to update name');
    }
  };

  if (!user) return <p>Loading trainer info...</p>;

  return (
    <div className="trainer-page">
      <h2>Trainer Profile</h2>
      {message && <p className="message">{message}</p>}

      <div className="profile-section">
        <label>Name:</label>
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
        <button onClick={handleNameChange}>Update Name</button>
      </div>

      <div className="profile-section">
        <label>Email:</label>
        <input type="text" value={email} disabled />
      </div>

      <div className="profile-section">
        <h3>Availability Slots</h3>
        <ul>
          {availableSlots.map((slot, index) => (
            <li key={index}>{slot.date} at {slot.time}</li>
          ))}
        </ul>
      </div>

      <div className="profile-section">
        <h3>Add New Slot</h3>
        <input type="date" value={date} onChange={(e) => setDate(e.target.value)} />
        <input type="time" value={time} onChange={(e) => setTime(e.target.value)} />
        <button onClick={handleAddSlot}>Add Slot</button>
      </div>
    </div>
  );
};

export default TrainerPage;
