import React, { useEffect, useState } from "react";
import { collection, getDocs, doc, updateDoc } from "firebase/firestore";
import { auth, db } from "../firebase";
import { onAuthStateChanged } from "firebase/auth";
import "./TrainerBooking.css";

const TrainerBooking = () => {
  const [user, setUser] = useState(null);
  const [trainers, setTrainers] = useState([]);
  const [selectedTrainer, setSelectedTrainer] = useState(null);
  const [selectedSlot, setSelectedSlot] = useState(null);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState("");

  useEffect(() => {
    // Track auth state
    const unsubscribeAuth = onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
    });

    // Fetch trainers
    const fetchTrainers = async () => {
      try {
        const querySnapshot = await getDocs(collection(db, "trainers"));
        const trainerList = querySnapshot.docs.map((doc) => ({
          id: doc.id,
          ...doc.data(),
        }));
        setTrainers(trainerList);
      } catch (error) {
        console.error("Error fetching trainers:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchTrainers();

    return () => unsubscribeAuth();
  }, []);

  const handleBooking = async (e) => {
    e.preventDefault(); // Prevent form refresh
    if (!user) {
      setMessage("You must be logged in to book a trainer.");
      return;
    }
    if (!selectedTrainer || !selectedSlot) {
      setMessage("Please select a trainer and a slot.");
      return;
    }

    try {
      const userRef = doc(db, "users", user.uid);
      await updateDoc(userRef, {
        trainerBooking: {
          trainerId: selectedTrainer.id,
          trainerName: selectedTrainer.name,
          date: selectedSlot.date,
          time: selectedSlot.time,
        },
      });
      setMessage(
        `Booking confirmed with ${selectedTrainer.name} on ${selectedSlot.date} at ${selectedSlot.time}`
      );
    } catch (error) {
      console.error("Booking failed:", error);
      setMessage("Booking failed. Try again.");
    }
  };

  if (loading) return <p>Loading trainers...</p>;

  return (
    <div className="trainer-booking">
      <h2>Trainer Booking</h2>
      {message && <p className="message">{message}</p>}

      <div className="trainer-list">
        {trainers.map((trainer) => (
          <div
            key={trainer.id}
            className={`trainer-card ${selectedTrainer?.id === trainer.id ? "selected" : ""}`}
            onClick={() => setSelectedTrainer(trainer)}
          >
            <h3>{trainer.name}</h3>
            <p>{trainer.availableSlots?.length || 0} slots available</p>
          </div>
        ))}
      </div>

      {selectedTrainer && (
        <div className="slot-selection">
          <h3>Available Slots for {selectedTrainer.name}</h3>
          <ul>
            {selectedTrainer.availableSlots.map((slot, index) => (
              <li
                key={index}
                className={`slot ${selectedSlot === slot ? "selected" : ""}`}
                onClick={() => setSelectedSlot(slot)}
              >
                {slot.date} at {slot.time}
              </li>
            ))}
          </ul>
        </div>
      )}

      <button className="booking-button" onClick={handleBooking}>
        Confirm Booking
      </button>
    </div>
  );
};

export default TrainerBooking;
